import '../styles/login.css';
import logo from '../imagenes/chefluzdelicias.png';
import { Component } from 'react';
import React, { useState } from "react";
import axios from 'axios';
import md5 from 'md5';

const baseUrl="http://localhost:27017/";

class LogIn extends Component{
    state={
        form:{
            username: '',
            password: '',
        }
    }
    handleChange=async e=>{
        await this.setState({
            form:{
                ...this.state.form,
                [e.target.name]: e.target.value
            }
        });
        // console.log(this.state.form);
    }
    //metodo para hacer peticion http e iniciar sesion

    iniciarSesion=async()=>{
        await axios.get(baseUrl, {params: {username: this.state.form.username, password: md5(this.state.form.password)}}) //cifrar el input a md5
        .then(response=>{
            console.log(response.data); //si es exitosa que se muestre en consola
        })
        .catch(error=>{
            console.log(error); //si es erronea que se muestre en consola 
        })
    }

    render() {
        return (
        <body>        
        <div id="contenedor">
            <div id="central">
                <div id="login">
                    {/* Imagen de la empresa */}
                    <div class= "logochef" ><img src={logo} alt='logo'/>                                                                
                    </div>
                    <div class="titulo">
                        Bienvenido, por favor inicia sesión
                    </div>
                    {/* Formulario de ingreso */}
                    <form id="loginform">
                        <input 
                        id="username" 
                        type="text" 
                        name="usuario" 
                        onChange={this.handleChange}
                        placeholder="Usuario"
                        required></input>

                        <input 
                        id="password" 
                        type="password" 
                        placeholder="Contraseña" 
                        name="Contraseña" 
                        onChange={this.handleChange}
                        required></input>

                        <button type="submit" title="Ingresar" name="Ingresar" id="buttonSubmit" onClick={()=> this.iniciarSesion()}>Iniciar</button>
                    </form>                    
                </div>               
            </div>
        </div>

    </body>

        )
    }
}





export default LogIn;
